package com.Linkdin.linkdinbackend.features.feed.model;

import com.Linkdin.linkdinbackend.features.authentication.model.AuthenticationUser;
import com.fasterxml.jackson.annotation.JsonIgnore;
import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;
import org.hibernate.annotations.CreationTimestamp;

import java.time.LocalDateTime;

// ğŸ§± Entity means this class will become a table in the database!
// ğŸ’¬ Every comment made by a user will be stored here.
@Setter
@Getter
@Entity(name = "comments")
public class Comment {

    // ğŸ†” Primary key of the table! Auto-generated by the database ğŸš€
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    // ğŸ”— Relationship: MANY comments belong to ONE post ğŸ“Œ
    // â— This field links each comment to its parent post
    // ğŸ›‘ We use @JsonIgnore to avoid infinite loops when converting to JSON (avoid recursion).
    @ManyToOne
    @JoinColumn(name = "post_id", nullable = false)
    @JsonIgnore
    private Post post;

    // ğŸ§‘â€ğŸ’» Relationship: MANY comments can be written by ONE user ğŸ‘¤
    // â• It connects the comment to the author (AuthenticationUser)
    @ManyToOne
    @JoinColumn(name = "author_id", nullable = false)
    private AuthenticationUser author;

    // âœï¸ The actual comment content (like "Nice post! ğŸ˜„")
    @Column(nullable = false)
    private String content;

    // ğŸ•°ï¸ Automatically captures the time the comment was created
    // âœ¨ Magic provided by Hibernateâ€™s @CreationTimestamp
    @CreationTimestamp
    private LocalDateTime creationDate;

    // ğŸ•°ï¸ Stores the last time the comment was updated (if edited)
    private LocalDateTime updatedDate;

    // ğŸ› ï¸ Default constructor â€“ required by JPA
    public Comment() {
    }

    // ğŸ¯ Custom constructor to quickly create a comment object
    public Comment(Post post, AuthenticationUser author, String content) {
        this.post = post;
        this.author = author;
        this.content = content;
    }

    // ğŸ•°ï¸ This method is called automatically just before an update happens
    // ğŸ“Œ It updates the `updatedDate` so we always know the last edit time
    @PreUpdate
    public void preUpdate() {
        this.updatedDate = LocalDateTime.now();
    }

    // â¬‡ï¸ Standard Getters and Setters â¬‡ï¸
    // ğŸ“¦ Get and set the values of all the fields
    // ğŸšª These are like the in/out gates for data

}

